package assignment1;

public class Number_pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int i=4;i>0;i--) {
	int c=i;
	while(4-c!=0) {
		System.out.println("");
		c++;
		
	}
	for(int j=i;j>1;j--)
	{
		System.out.println(1);
		System.out.println(0);
	}
	System.out.println(1);

	System.out.println("");
}
	}

}
